package java1;

public class Implicittypecasting {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int intNumber = 100;
		long longNumber;
		// Implicit casting from int to long
		longNumber = intNumber;
		System.out.println("Integervalue:" + intNumber);
		System.out.println("Long value after implimenting casting:" + longNumber);
	}

	}


